constexpr inline uint64_t int_power(uint64_t val, uint8_t power) noexcept {
	if (power == 1) {
		return val;
	}

	uint64_t x = val;
	uint64_t result = 1;
	while (power > 0) {
		if (power & 0x01) {
			result *= x;
		}

		x = x * x;
		power >>= 1;
	}

	return result;
}

/// source https://github.com/WojciechMula/toys/blob/master/avx512-binomialcoef/avx512_naive.cpp
constexpr inline uint64_t simd_binom(const uint64_t n, const uint64_t k) noexcept {
	ASSERT(n > 1);
	ASSERT(n < cryptanalysislib::internal::factorials_count);
	ASSERT(k > 1);
	ASSERT(k < cryptanalysislib::internal::factorials_count);
	ASSERT(k < n);

	// n!
	uint8x64_t fn = uint8x64_t::aligned_load(cryptanalysislib::internal::factorials[n]);
	// k!
	uint8x64_t fk = uint8x64_t::aligned_load(cryptanalysislib::internal::factorials[k]);
	// (n - k)!
	uint8x64_t fnk = uint8x64_t::aligned_load(cryptanalysislib::internal::factorials[n - k]);

	const uint8x64_t vdenom = fk + fnk;// k! * (n -k)!
	const uint8x64_t tmp = fn - vdenom;// n! / ...

	alignas(64) int8_t coef[64];
	uint8x64_t::aligned_store(coef, tmp);

	// get back from the product of factorials into the integer domain
	uint64_t num = 1;
	uint64_t denom = 1;
	for (uint32_t i = 0; i < 64; i++) {
		if (coef[i] > 0) {
			num *= int_power(cryptanalysislib::internal::primes[i], coef[i]);
		} else if (coef[i] < 0) {
			denom *= int_power(cryptanalysislib::internal::primes[i], -coef[i]);
		}
	}

	return num / denom;
}
